sys_ui_action.script:
(function(action){
	var triggers = {
		'x_dete_emb_edded_core_form': ['x_dete_emb_edded_core_form'],
		'x_dete_emb_edded_core_jtemplate': ['x_dete_emb_edded_core_jtemplate'],
		'x_dete_emb_edded_plugin_form': ['x_dete_emb_edded_plugin_form'],
		'x_dete_emb_edded_plugin_jtemplate': ['x_dete_emb_edded_plugin_jtemplate'],
		'x_dete_emb_edded_jelly': ['x_dete_emb_edded_core_form',
								  'x_dete_emb_edded_core_jtemplate',
								  'x_dete_emb_edded_plugin_form',
								  'x_dete_emb_edded_plugin_jtemplate'
								  ],
		'x_dete_emb_edded_plugin_jelly': ['x_dete_emb_edded_plugin_form',
										 'x_dete_emb_edded_plugin_jtemplate'
										 ],
		'x_dete_emb_edded_core_js': ['x_dete_emb_edded_core_js'],
		'x_dete_emb_edded_plugin_js': ['x_dete_emb_edded_plugin_js']
	};
	var table = action.getGlideURI().getFileFromPath();
	var i = table.indexOf("_list");
	if (i>-1)
		table = table.substr(0,i);
	if (gs.nil(triggers[table]))
		throw Error("Trigger not defined for this table.");
	try{
		for (i=0;i<triggers[table].length;i++){
			gs.eventQueue('x_dete_emb.edded_content_ui_action',null,'jelly',triggers[table][i]);			
		}
		gs.addInfoMessage("Update request sent");
	} catch (e) {
	    gs.addErrorMessage("Error sending update request event "+ e.message);
	}
})(action);
