sys_ui_page.client_script:
var makeFileLink = function() {
    var fileElement = document.getElementById('file_content');
    var targetElement = document.getElementById(fileElement.getAttribute('data-target'));
    var a = document.createElement("a");
    a.innerText = fileElement.getAttribute('data-linktext');
    var myblob = new Blob([atob(fileElement.innerText)], { type: 'application/octet-stream' });
    var url = window.URL.createObjectURL(myblob);
    a.href = url;
    a.download = fileElement.getAttribute('data-filename');
    targetElement.appendChild(a);
};
